function profile(first, last, level, exp ){
    this.firstName = first;
    this.lastName = last;
    this.level = level;
    this.experience = exp;
    this.dailyExp = exp;
    this.account = function() {return this.firstName + " " + this.lastName;};
}

function addExp(exp){
    	this.experience += exp;
    	if(this.experience >= 100){
    		this.level += exp;
    		this.experience = 0;
	    }
		if(new Date() != d){
			dailyExp += 50;
			d = new Date();
		}
    }

